export const AlgorithmActions = {
  INFECT_HOMES: "INFECT_HOMES",
};

export const InfectHomes = () => (dispatch, getState) => {
  console.log(getState());
  const useLightMode = getState().algorithmReducer.useLightMode;
  console.log(useLightMode);
  dispatch({
    type: AlgorithmActions.INFECT_HOMES,
    key: "useLightMode",
    value: !useLightMode,
  });
};
