import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { InfectHomes } from "../../redux/actions/algorithmActions";
import { StyledUl, Button, StyledDiv } from "./Styled";
import { HealthyHome, ZombieHome, EmptyHome } from "../../svg-icons";

const ScenarioTemplate = ({ scenario, text, day }) => {
  const data = useSelector((state) => state.AlgorithmReducer.firstScenarioData);
  const __ = useSelector((state) => state.AlgorithmReducer.__);

  if (scenario === 0) {
    console.log("home", data, __);
  }

  const dispatch = useDispatch();
  const scenarioItems = data.map((row, index) => {
    return (
      <StyledUl key={index}>
        <li key={`${scenario}-${index}`}>
          {row.map((column, i) => {
            const k = `${scenario}-${index}-${i}`;
            if (column === 2) {
              return <ZombieHome key={k} />;
            } else if (column === 1) {
              return <HealthyHome key={k} />;
            } else {
              return <EmptyHome key={k} />;
            }
          })}
        </li>
      </StyledUl>
    );
  });

  return (
    <StyledDiv>
      <p>Day: </p>
      <p>{text}</p>
      {scenarioItems}
      <Button onClick={() => dispatch(InfectHomes(scenario))}>
        Play animation
      </Button>
    </StyledDiv>
  );
};

export default ScenarioTemplate;
