import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { InfectHomes } from "../../redux/actions/algorithmActions";
import { StyledUl, Button, StyledDiv } from "./Styled";
import { HealthyHome, ZombieHome, EmptyHome } from "../../svg-icons";

const ScenarioTemplate = ({ scenario, text, day }) => {
  const data = useSelector((state) => {
    return state.AlgorithmReducer.firstScenarioData;
  });
  console.log("home", firstScenarioData);

  // const secondScenarioData = useSelector((state) => {
  //   return state.AlgorithmReducer.secondScenarioData;
  // });

  const dispatch = useDispatch();
  let lastId = 0;
  const scenarioItems = data.map((i, number) => {
    return (
      <StyledUl key={number}>
        <li key={lastId}>
          {data[number].map((i) => {
            lastId++;
            if (i === 2) {
              return <ZombieHome />;
            } else if (i === 1) {
              return <HealthyHome />;
            } else {
              return <EmptyHome />;
            }
          })}
        </li>
      </StyledUl>
    );
  });

  return (
    <StyledDiv>
      <p>Day: </p>
      <p>{text}</p>
      {scenarioItems}
      <Button onClick={() => dispatch(InfectHomes(scenario))}>
        Play animation
      </Button>
    </StyledDiv>
  );
};

export default ScenarioTemplate;
