import React from "react";
import { useSelector, useDispatch } from "react-redux";

import { Header, IconGuide, Text, Box } from "./Styled";
import { HealthyHome, ZombieHome, EmptyHome } from "../../svg-icons";
import ScenarioTemplate from "./ScenarioTemplate";

const Home = () => {
  const firstScenarioData = useSelector((state) => {
    return state.AlgorithmReducer.firstScenarioData;
  });
  const secondScenarioData = useSelector((state) => {
    return state.AlgorithmReducer.secondScenarioData;
  });

  return (
    <>
      <Header>Zombies</Header>
      <IconGuide>
        <EmptyHome width="40" />
        <Text>Empty Home</Text>
        <HealthyHome width="40" />
        <Text>Healthy Home</Text>
        <ZombieHome width="40" />
        <Text>Zombie Home</Text>
      </IconGuide>

      <Box>
        <ScenarioTemplate
          data={firstScenarioData}
          text="A case where all can be infected"
          scenario={0}
          // day={day}
        />
        <ScenarioTemplate
          data={secondScenarioData}
          text="A case with effective social distancing"
          scenario={1}

          // day={day}
        />
      </Box>
    </>
  );
};

export default Home;
