import styled from "styled-components/macro";
import media from "../../helpers/media";

export const IconGuide = styled.div`
  display: flex;
  justify-content: center;
  margin-top: 48px;
  margin-left: 18px;

  ${media.up.medium`
     margin-left: 0;
  `};
`;

export const HeaderContainer = styled.h2`
  text-align: center;
`;

export const Text = styled.p`
  margin: auto 10px;
`;

export const Box = styled.div`
  display: grid;
  text-align: center;
  column-gap: 10px;
  margin-top: 48px;

  ${media.up.medium`
    grid-template-columns: 1fr 1fr;
    margin: 100px 120px;
  `};
`;

export const StyledUl = styled.ul`
  display: flex;
  list-style: none;
  padding: 0;
  justify-content: center;
`;
